import { createElement } from 'overmind-components'

import { Component } from '../../../overmind'
import * as styles from './styles'

type Props = {
  onClick?: () => void
  type?: 'button' | 'submit'
  disabled?: boolean
  children: string
}

const Button: Component<Props> = ({ type, disabled, onClick, children }) => (
  <button
    className={styles.button}
    type={type}
    disabled={disabled}
    onClick={onClick ? () => onClick() : null}
  >
    {children}
  </button>
)

export default Button
