const electron = require('electron')
const app = electron.app
const appVersion = app.getVersion()
const BrowserWindow = electron.BrowserWindow
const path = require('path')
const DevtoolBackend = require('overmind-devtools-client/DevtoolBackend')
const storage = require('electron-json-storage')
const prompt = require('electron-prompt')

process.env.ELECTRON_DISABLE_SECURITY_WARNINGS = 'true'

function createWindow() {
  const mainWindow = new BrowserWindow({
    icon: path.resolve('icons', 'icon.png'),
    height: 768,
    width: 768,
    minHeight: 500,
    minWidth: 500,
  })

  const devtoolBackend = DevtoolBackend.create({
    onRelaunch() {
      app.relaunch()
      app.quit()
    },
    storage,
  })

  function startDevtoolBackend() {
    return new Promise((resolve) => {
      storage.get('port', (_, port) => {
        port = typeof port === 'string' ? Number(port) : 3031

        devtoolBackend
          .connect(port)
          .then(() => resolve(port))
          .catch(() => {
            prompt({
              title: 'Please provide a new port',
              label: 'Port number:',
              value: '',
            })
              .then((port) => {
                if (port) {
                  storage.set('port', port, () => {
                    startDevtoolBackend().then(resolve)
                  })
                }
              })
              .catch(console.error)
          })
      })
    })
  }

  mainWindow.on('closed', () => app.quit())

  electron.Menu.setApplicationMenu(
    electron.Menu.buildFromTemplate([
      {
        label: 'Application',
        submenu: [
          { label: 'Overmind Devtools v' + appVersion },
          { type: 'separator' },
          {
            label: 'Debug',
            click() {
              mainWindow.openDevTools()
            },
          },
          {
            label: 'Learn More',
            click() {
              require('electron').shell.openExternal('https://overmindjs.org')
            },
          },
          {
            label: 'License',
            click() {
              require('electron').shell.openExternal(
                'https://github.com/cerebral/overmind/blob/master/LICENSE'
              )
            },
          },
          { type: 'separator' },
          { role: 'quit' },
        ],
      },
      {
        label: 'Edit',
        submenu: [
          { role: 'undo' },
          { role: 'redo' },
          { type: 'separator' },
          { role: 'cut' },
          { role: 'copy' },
          { role: 'paste' },
          { role: 'selectall' },
        ],
      },
    ])
  )

  startDevtoolBackend().then((port) => {
    mainWindow.loadURL(
      'data:text/html;charset=UTF-8,' +
        encodeURIComponent(
          devtoolBackend.getMarkup(
            process.env.NODE_ENV === 'production'
              ? 'http://localhost:8080/bundle.js'
              : 'bundle.js',
            port
          )
        ),
      {
        baseURLForDataURL: `file://${path.resolve()}/devtoolsDist/`,
      }
    )
    if (process.env.NODE_ENV !== 'production') {
      mainWindow.webContents.openDevTools()
    }
  })
}

// This method will be called when Electron has finished
// initialization and is ready to create browser windows.
// Some APIs can only be used after this event occurs.
app.on('ready', createWindow)
