import { deepCopy } from './utils'

describe('deepCopy', () => {
  test('should be able to preserve getters', () => {
    expect.assertions(1)
    const original = {
        value: 0,
        get valuePlusTwo() { return this.value + 2 }
    }
    const copy = deepCopy(original)
    copy.value = 15
    expect(copy.valuePlusTwo).toBe(17)
  })
})
